import { Image, message, Upload } from 'antd';
import ImgCrop from 'antd-img-crop';
import React, { useState } from 'react';
import { LoadingOutlined, PlusOutlined } from '@ant-design/icons';
import { useModel } from '@@/plugin-model/useModel';
import { RcFile } from 'antd/es/upload';
export default () => {
  const { initialState, refresh } = useModel('@@initialState');
  const [imageUrl, setImageUrl] = useState<string>(initialState.data.head);
  const [loading, setLoading] = useState<boolean>(false);

  const handleChange = (info: any) => {
    if (info.file.status === 'uploading') {
      setLoading(true);
      return;
    }
    console.log(info);
    if (info.file.response) {
      //只有存在返回值
      if (info.file.response.result) {
        setLoading(false);
        setImageUrl(info.file.response.data);
        message.success(info.file.response.message);
        refresh();
      } else {
        //后台错误，一般指图片重复
        message.error(info.file.response.message);
        return false;
      }
    }
  };
  //图片上传之前判断
  const onBeforeUpload = (file: RcFile) => {
    if (file.type !== 'image/png' && file.type !== 'image/jpeg') {
      message.error('仅支持上传jpg/png格式的文件');
      return false;
    }
    if (file.size / 1024 / 1024 > 8) {
      message.error('上传图片不能超过8M!');
      return false;
    }
    return true;
  };
  const uploadButton = (
    <div>
      {loading ? <LoadingOutlined /> : <PlusOutlined />}
      <div style={{ marginTop: 8 }}>Upload</div>
    </div>
  );
  return (
    <div style={{ width: '100%', overflow: 'hidden' }}>
      <ImgCrop rotate>
        <Upload
          name="avatar"
          listType="picture"
          className="avatar-uploader"
          showUploadList={false}
          data={{ uid: initialState.data.uid }}
          action="http://localhost:8088/upload/avatar"
          beforeUpload={onBeforeUpload}
          onChange={handleChange}
          maxCount={1}
        >
          {imageUrl ? (
            <img
              src={'http://localhost:8088/image/' + imageUrl}
              alt="avatar"
              style={{ width: '100%' }}
            />
          ) : (
            uploadButton
          )}
        </Upload>
      </ImgCrop>

      {/*<Image*/}
      {/*  width={"100%"}*/}
      {/*  onClick={onClick}*/}
      {/*  src={"error"+ initialState.data.head}*/}
      {/*  // fallback="data:image/png;base64,"*/}
      {/*/>*/}
    </div>
  );
};
